import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  TouchableOpacity,
  Image,
  StatusBar,
  ImageBackground,
  Dimensions,
  TouchableHighlight,
  Animated,
  PanResponder
} from "react-native";
import { Ionicons } from '@expo/vector-icons';
import {
  PanGestureHandler,
  PinchGestureHandler,
  State,
} from 'react-native-gesture-handler';



class sevenpage extends React.Component {
  
  pinchRef = React.createRef();

  constructor(props) {
    super(props);

    this._baseScale = new Animated.Value(1);
    this._pinchScale = new Animated.Value(1);
    this._scale = Animated.multiply(this._baseScale, this._pinchScale);
    this._lastScale = 1;
    this._onPinchGestureEvent = Animated.event(
      [{ nativeEvent: { scale: this._pinchScale } }],
      { useNativeDriver: false }
    );


    this._translateX = new Animated.Value(0);
    this._translateY = new Animated.Value(0);
    this._lastOffset = { x: 0, y: 0 };
    this._onGestureEvent = Animated.event(
      [
        null,{
          
            translationX: this._translateX,
            translationY: this._translateY,
          
        },
      ],
      { useNativeDriver: false }
    );


    this.state = {
        imageIndex: 0,
        isImageViewVisible: false,
    } 

  }


  _onPinchHandlerStateChange = event => {
    if (event.nativeEvent.oldState === State.ACTIVE) {
      this._lastScale *= event.nativeEvent.scale;
      this._baseScale.setValue(this._lastScale);
      this._pinchScale.setValue(1);
    }
  };

  _onHandlerStateChange = event => {
  
      this._lastOffset.x.flattenOffset();
      this._lastOffset.y.flattenOffset();
      this._translateX.setOffset(this._translateX._value);
      this._translateY.setOffset(this._translateY._value);
    
  };


  render() {
    const images = [this.props.route.params?.someParam ?? 'defaultValue'];
    
    return (
        <View style={{ flex: 1 }}>
        <SafeAreaView
          style={{
            paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0, //對齊上面
          }}
        />
        <View>
          <ImageBackground
            style={styles.background}
            source={require("./assets/background.png")}
          > 
              {/*upperline (back and save) */}
              <View style={{flexDirection : "row",marginTop : "4%",marginLeft : "6%"}}>
                <TouchableOpacity onPress={() => {
                    this.props.navigation.navigate("fifthpage");}}>
                  <Ionicons name="arrow-back" size={26*rem} color="#484848" />
                </TouchableOpacity>

                
                <TouchableOpacity onPress={()=>{alert(this.state.pan.x.dx)}}>
                    <Text style={styles.text}>Send</Text>
                </TouchableOpacity>
              
              </View>      

              
                  <View style={styles.slide1}>
                      <ImageBackground source={images[0].source} style={styles.image}>
                      <PanGestureHandler {...this.props} onGestureEvent={this._onGestureEvent}
        o               nHandlerStateChange={this._onHandlerStateChange} >

                

                        <Animated.View style={[{transform: [{ perspective: 200 }, { scale: this._scale },{ translateX: this._translateX },
                          { translateY: this._translateY }]}]} >
                            
                            <Image source={require('./assets/model.png')} ></Image>
                            
                        </Animated.View> 
                        
                        

                      </PanGestureHandler>
                      </ImageBackground>
                  </View>

              </ImageBackground> 
                
          </View>
      </View>
    );
  }
}
  

export default sevenpage;

const windowWidth = Dimensions.get("window").width;
const windowHeight = Dimensions.get("window").height;
const rem = windowWidth / 380;

const styles = StyleSheet.create({
  //style for upperline
  background : {
    height : windowHeight,
    width : windowWidth,
  },
  text : {
    marginLeft : "78%",
    fontSize : 18*rem,
    color : "#484848",
    fontWeight : "bold",
  },

  //put the styles you defined here

  //style for home button
  home : {
    backgroundColor :"#7FA6A6",
    width : "68%",
    height : "24%",
    marginRight : "16%",
    borderRadius: 10 * rem,
    justifyContent: "center",
    alignItems: "center",  
 },
 //style for right button
 button :{
    padding : "2%",
    flexDirection : "row",
    backgroundColor :"#484848",
    marginLeft : "8%",
    width : "80%",
    height : "24%",
    borderRadius: 10 * rem,
    justifyContent: "center",
    alignItems: "center",  
 },
 buttontext : {
    color : "#EFEFEF",
    fontSize : 12*rem,
    marginLeft : "4%",
 },

 slide1: {

  backgroundColor: '#FFFFFF',
  justifyContent: 'center',
  alignItems: 'center',
  marginTop: '5%',
  height: '88%',

  shadowColor: "#000000",
  shadowOffset: {
    width: 0,
    height: 3,
  },
  shadowOpacity: 0.4,
  shadowRadius: 5,
  elevation: 7,
  },
  image: {
  height: '98%',
  width: '98%',
  resizeMode: 'cover',
  marginLeft:'2%',
  marginTop:'1%'
  },
  ImageButton: {
    marginTop: '10%',
    marginLeft: "10%",
    height: '70%',
    width: '80%',
  }

});
