import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  TouchableOpacity,
  Image,
  StatusBar,
  ImageBackground,
  Dimensions,
  TouchableHighlight,
  Animated,
  PanResponder
} from "react-native";
import { Ionicons } from '@expo/vector-icons';
import  {PanGestureHandler, PinchGestureHandler} from 'react-native-gesture-handler'
import {GestureHandler} from 'react-native-gesture-handler';


class sevenpage extends React.Component {
  

  constructor(props) {
    super(props);


    this.state = {
        imageIndex: 0,
        isImageViewVisible: false,
    } 

  };

  pan= new Animated.ValueXY();
  scale = new Animated.Value(1);

      
  panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onPanResponderGrant: () => {
      this.pan.setOffset({
        x: this.pan.x._value,
        y: this.pan.y._value
      });
    },
    onPanResponderMove: Animated.event([
      null,
      {
        dx: this.pan.x, // x,y are Animated.Value
        dy: this.pan.y,
        nativeEvent:{scale: this.scale}
      }
    ],{useNativeDriver: false}),
    onPanResponderRelease: () => {
        this.pan.flattenOffset();
    }
  });


  render() {
    const {isImageViewVisible, imageIndex} = this.state;
    const images = [this.props.route.params?.someParam ?? 'defaultValue'];
    const isFocused  = this.props.navigation.isFocused();


    return (
        <View style={{ flex: 1 }}>
        <SafeAreaView
          style={{
            paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0, //對齊上面
          }}
        />
        <View>
          <ImageBackground
            style={styles.background}
            source={require("./assets/background.png")}
          > 
              {/*upperline (back and save) */}
              <View style={{flexDirection : "row",marginTop : "4%",marginLeft : "6%"}}>
                <TouchableOpacity onPress={() => {
                    this.props.navigation.navigate("fifthpage");}}>
                  <Ionicons name="arrow-back" size={26*rem} color="#484848" />
                </TouchableOpacity>

                
                <TouchableOpacity onPress={()=>{alert(this.state.pan.x.dx)}}>
                    <Text style={styles.text}>Send</Text>
                </TouchableOpacity>
              
              </View>      

              
                  <View style={styles.slide1}>
                      <ImageBackground source={images[0].source} style={styles.image}>
          
                      <PinchGestureHandler onGestureEvent={this.onPinchEvent}>       
                        <Animated.Image source={require('./assets/model.png')}
                        {...this.panResponder.panHandlers} style={[this.pan.getLayout(),{transform:[{scale: this.scale}]}]} />

                      </PinchGestureHandler>
                      
                      </ImageBackground>
                  </View>

              </ImageBackground> 
                
          </View>
      </View>
    );
  }
}
  

export default sevenpage;

const windowWidth = Dimensions.get("window").width;
const windowHeight = Dimensions.get("window").height;
const rem = windowWidth / 380;

const styles = StyleSheet.create({
  //style for upperline
  background : {
    height : windowHeight,
    width : windowWidth,
  },
  text : {
    marginLeft : "78%",
    fontSize : 18*rem,
    color : "#484848",
    fontWeight : "bold",
  },

  //put the styles you defined here

  //style for home button
  home : {
    backgroundColor :"#7FA6A6",
    width : "68%",
    height : "24%",
    marginRight : "16%",
    borderRadius: 10 * rem,
    justifyContent: "center",
    alignItems: "center",  
 },
 //style for right button
 button :{
    padding : "2%",
    flexDirection : "row",
    backgroundColor :"#484848",
    marginLeft : "8%",
    width : "80%",
    height : "24%",
    borderRadius: 10 * rem,
    justifyContent: "center",
    alignItems: "center",  
 },
 buttontext : {
    color : "#EFEFEF",
    fontSize : 12*rem,
    marginLeft : "4%",
 },

 slide1: {

  backgroundColor: '#FFFFFF',
  justifyContent: 'center',
  alignItems: 'center',
  marginTop: '5%',
  height: '88%',

  shadowColor: "#000000",
  shadowOffset: {
    width: 0,
    height: 3,
  },
  shadowOpacity: 0.4,
  shadowRadius: 5,
  elevation: 7,
  },
  image: {
  height: '98%',
  width: '98%',
  resizeMode: 'cover',
  marginLeft:'2%',
  marginTop:'1%'
  },
  ImageButton: {
    marginTop: '10%',
    marginLeft: "10%",
    height: '70%',
    width: '80%',
  }

});
